import React from 'react';

const Testimonials = () => {
  return (
    <section style={styles.section}>
      <div style={styles.textContainer}>
        <h2 style={styles.heading}>Cosa dicono di noi?</h2>
        <div style={styles.testimonials}>
          <div style={styles.testimonial}>
            <p style={styles.text}>"Roberta è una esperta e preparata professionista, dispensa sempre ottimi consigli! Consiglio il suo centro estetico per la vasta gamma dei trattamenti offerti e per la bravura con cui vengono effettuati! Onestà, professionalità e simpatia la contraddistinguono"</p>
            <p style={styles.author}>- Emanuela I.</p>
          </div>
          <div style={styles.testimonial}>
            <p style={styles.text}>"Centro completo di tutti i servizi di estetica; la proprietaria, Roberta, è meravigliosa e super competente in tutto! Tratta le clienti con professionalità e accoglienza, resto sempre soddisfatta dopo ogni appuntamento. Consigliatissimo!"</p>
            <p style={styles.author}>- Francesca M.</p>
          </div>
        </div>
      </div>
    </section>
  );
};

const styles = {
  section: {
    background: 'url("https://www.formart.it/flex/AppData/Redational/corsi/u/n/i/D.5ae225fb9c99ceb47e34/OB_sfondo_estetica.jpg") no-repeat center center',
    backgroundSize: 'cover',  // L'immagine copre l'intera sezione
    backgroundAttachment: 'fixed',  // Effetto parallax, se vuoi l'immagine fissa
    height: '100vh',  // Imposta l'altezza a tutta la finestra del browser
    width: '100%',  // Imposta la larghezza al 100% dello schermo
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff',
    textAlign: 'center',
  },
  textContainer: {
    maxWidth: '900px', // Rende il contenitore più largo per adattarsi meglio ai contenuti orizzontali
    padding: '3rem',
    backgroundColor: 'rgba(0, 0, 0, 0.6)', // Sfondo scuro per migliorare il contrasto
    borderRadius: '10px',
    textAlign: 'center',
    marginTop: '50px', // Margine superiore per un miglior posizionamento
  },
  heading: {
    fontSize: '3.5rem',
    fontWeight: 'bold',
    color: '#fff',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)', // Ombra per migliorare la visibilità del testo
    marginBottom: '2rem',
  },
  text: {
    fontStyle: 'italic',
    fontSize: '1.1rem',
    marginBottom: '1rem',
  },
  author: {
    fontWeight: 'bold',
    fontSize: '1rem',
    marginTop: '1rem',
  },
  testimonials: {
    display: 'flex',
    justifyContent: 'space-between', // Posiziona le recensioni affiancate con spazio tra di loro
    gap: '2rem', // Distanza tra le recensioni
    marginTop: '2rem',
    flexWrap: 'wrap', // Permette alle recensioni di adattarsi su schermi più piccoli
  },
  testimonial: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '8px',
    width: '48%', // Ogni recensione occupa il 48% della larghezza del contenitore
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
};

export default Testimonials;
