import React from 'react';
import Slider from 'react-slick';

const AboutUs = () => {
  const settings = {
    dots: false, // Rimuove i puntini di navigazione
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Attiva l'autoplay
    autoplaySpeed: 3000, // Imposta il tempo tra ogni slide
    fade: true, // Transizione di dissolvenza tra le slide
    arrows: false, // Disabilita le frecce
  };

  return (
    <section style={styles.section}>
      <h2 style={styles.heading}>Chi Siamo</h2>
      <p style={styles.text}>
        La Beauty Armony Academy, fondata da Roberta Caruso a Melito di Porto Salvo, Ã¨ un centro di formazione all'avanguardia nel campo dell'estetica e dei trattamenti di bellezza. Offriamo corsi pratici e teorici, pensati per chi desidera intraprendere una carriera professionale nell'estetica.
      </p>
      <div style={styles.imgContainer}>
      <Slider {...settings}>
          <div style={styles.slide}>
            <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/273964030_326262012769551_5873447625054097373_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=833d8c&_nc_ohc=ZNOb3CvcCnoQ7kNvgHuyzvH&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=Asj_gKWrjWNrMSGUVesru9k&oh=00_AYCkQiRp0O1IQS7oiDniTg8tAPyb0Y2Me_YBU-YluQJvtg&oe=675CFC5B" alt="Beauty Armony Academy 1" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/306121852_455532273175857_2259516523418864674_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=833d8c&_nc_ohc=oSTBSvKtD1IQ7kNvgFTbT32&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=AacsIi1vA5GACWm5DgmVms2&oh=00_AYAPWA9kGZ1jMIIiWz4rVQC6pLGBeq-B_0lzQn1FtDXrlg&oe=675CFFAD" alt="Beauty Armony Academy 2" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/328929211_733299641565186_6337394224976095839_n.jpg?_nc_cat=110&ccb=1-7&_nc_sid=833d8c&_nc_ohc=HIRZLEP9flkQ7kNvgFf0yD-&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=ABv2pOFreLSR1uxi9FCBsBG&oh=00_AYCSLGRP19M23Vz_UYplkQliyr90qVHgT_P7GWZVQmAWMw&oe=675D1235" alt="Beauty Armony Academy 3" style={styles.img} />
          </div>
        </Slider>
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #f8b400, #ff5722)',
    color: '#fff',
    borderRadius: '10px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: '1.5rem',
    letterSpacing: '1px',
  },
  text: {
    fontSize: '1.2rem',
    maxWidth: '800px',
    margin: '0 auto',
    marginBottom: '2rem',
    lineHeight: '1.6',
    color: '#fff',
  },
  imgContainer: {
    maxWidth: '1000px', // Assicurati che il contenitore sia abbastanza largo
    margin: '0 auto',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.2)',
    position: 'relative',
    height: '400px', // Imposta una altezza fissa per il carosello
  },
  slide: {
    position: 'relative',
  },
  img: {
    width: '100%', // L'immagine si adatta alla larghezza del contenitore
    height: '100%', // Imposta l'altezza al 100% del contenitore
    objectFit: 'cover', // Le immagini si adattano senza distorsioni
    borderRadius: '10px',
  },
};

export default AboutUs;
