import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Recensioni.css';

function Recensioni() {
  const [recensioni, setRecensioni] = useState([]);
  const [recensione, setRecensione] = useState('');
  const [nome_cliente, setNomeUtente] = useState('');
  const [valutazione, setRating] = useState(1);  // Rating predefinito
  const [error, setError] = useState(null);  // Stato per errori
  const [success, setSuccess] = useState(null);  // Stato per il messaggio di successo

  /* Carica le recensioni esistenti al caricamento della pagina
  useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni')
      .then(response => {
        console.log(response.data);  // Aggiungi un log per vedere i dati ricevuti
        setRecensioni(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni', error);
        setError('Errore nel recupero delle recensioni');
      });
}, []);*/
useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni')
      .then(response => setRecensioni(response.data.filter(r => r.stato === 'approvata')))
      .catch(error => console.error('Errore nel recupero delle recensioni', error));
  }, []);
  

  const handleSubmit = (e) => {
    e.preventDefault();

    // Crea l'oggetto recensione
    const nuovaRecensione = {
        nome_cliente: nome_cliente,
        recensione: recensione,
        valutazione: valutazione
      };
      

    // Invia la recensione al backend
    axios.post('http://localhost:5000/api/aggiungi-recensione', nuovaRecensione)
      .then(response => {
        console.log('Recensione aggiunta', response);
        // Aggiungi la nuova recensione alla lista senza ricaricare la pagina
        setRecensioni([...recensioni, response.data]);
        setNomeUtente('');  // Resetta il nome utente
        setRecensione('');   // Resetta la recensione
        setRating(1);        // Resetta il rating
        setSuccess('Recensione aggiunta con successo!'); // Messaggio di successo
        setError(null); // Resetta l'errore, se presente
      })
      .catch(error => {
        console.error('Errore nell\'invio della recensione', error);
        setError('Errore nell\'invio della recensione');
      });
  };

  return (
    <div className="recensioni-page">
      <h2>Recensioni</h2>

      {/* Messaggio di errore */}
      {error && <div className="error-message">{error}</div>}
      {/* Messaggio di successo */}
      {success && <div className="success-message">{success}</div>}

      {/* Form di invio recensione */}
      <form onSubmit={handleSubmit} className="form-recensione">
        <input
          type="text"
          placeholder="Il tuo nome"
          value={nome_cliente}
          onChange={(e) => setNomeUtente(e.target.value)}
          required
        />
        <textarea
          placeholder="Scrivi una recensione"
          value={recensione}
          onChange={(e) => setRecensione(e.target.value)}
          required
        />
        <select value={valutazione} onChange={(e) => setRating(Number(e.target.value))}>
          <option value={1}>1</option>
          <option value={2}>2</option>
          <option value={3}>3</option>
          <option value={4}>4</option>
          <option value={5}>5</option>
        </select>
        <button type="submit">Invia Recensione</button>
      </form>

      {/* Visualizza le recensioni approvate */}
      <ul>
        {recensioni.map((recensione) => (
          <li key={recensione.id}>
            <p><strong>Nome Utente:</strong> {recensione.nome_cliente}</p>
            <p><strong>Recensione:</strong> {recensione.recensione}</p>
            <p><strong>Rating:</strong> {recensione.valutazione}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Recensioni;
