// client/src/components/Servizi.js
import React, { useEffect, useState } from 'react';
import './Servizi.css';

const Servizi = () => {
  const [servizi, setServizi] = useState([]);
  const [newService, setNewService] = useState({
    titolo: '',
    descrizione: '',
    prezzo: '',
    durata: '',
    immagine_url: '',
  });

  // Carica i servizi dal back-end
  useEffect(() => {
    fetch('http://localhost:5000/api/servizi')
      .then((response) => response.json())
      .then((data) => setServizi(data))
      .catch((error) => console.error('Errore nel recupero dei servizi:', error));
  }, []);

  // Aggiungi un nuovo servizio
  const addService = () => {
    fetch('http://localhost:5000/api/servizi', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newService),
    })
      .then((response) => response.json())
      .then(() => {
        // Ricarica i servizi dopo aver aggiunto uno nuovo
        fetch('http://localhost:5000/api/servizi')
          .then((response) => response.json())
          .then((data) => setServizi(data));
      })
      .catch((error) => console.error('Errore nell\'aggiunta del servizio:', error));
  };

  // Elimina un servizio
  const deleteService = (id) => {
    fetch(`http://localhost:5000/api/servizi/${id}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then(() => {
        // Ricarica i servizi dopo aver eliminato uno
        fetch('http://localhost:5000/api/servizi')
          .then((response) => response.json())
          .then((data) => setServizi(data));
      })
      .catch((error) => console.error('Errore nell\'eliminazione del servizio:', error));
  };

  return (
    <div className="servizi-page">
      <section id="servizi" className="servizi-section">
        <h2>I Nostri Servizi</h2>
        <p className="section-description">
          Offriamo una vasta gamma di trattamenti per il viso, il corpo e il benessere generale. Scegli quello che fa per te!
        </p>

        <div className="servizi-list">
          {servizi.map((servizio) => (
            <div className="servizio-card" key={servizio.id}>
              <img src={servizio.immagine_url} alt={servizio.titolo} />
              <h3>{servizio.titolo}</h3>
              <p>{servizio.descrizione}</p>
              <p><strong>Prezzo:</strong> €{servizio.prezzo}</p>
              <p><strong>Durata:</strong> {servizio.durata} minuti</p>
              <div className="servizio-buttons">
                <button className="btn prenota">Prenota</button>
                <button className="btn scopri">Scopri di più</button>
                <button onClick={() => deleteService(servizio.id)} className="btn elimina">Elimina</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Form per aggiungere un servizio */}
      <section className="aggiungi-servizio">
        <h3>Aggiungi un Servizio</h3>
        <input
          type="text"
          placeholder="Titolo"
          value={newService.titolo}
          onChange={(e) => setNewService({ ...newService, titolo: e.target.value })}
        />
        <input
          type="text"
          placeholder="Descrizione"
          value={newService.descrizione}
          onChange={(e) => setNewService({ ...newService, descrizione: e.target.value })}
        />
        <input
          type="number"
          placeholder="Prezzo"
          value={newService.prezzo}
          onChange={(e) => setNewService({ ...newService, prezzo: e.target.value })}
        />
        <input
          type="number"
          placeholder="Durata"
          value={newService.durata}
          onChange={(e) => setNewService({ ...newService, durata: e.target.value })}
        />
        <input
          type="text"
          placeholder="Immagine URL"
          value={newService.immagine_url}
          onChange={(e) => setNewService({ ...newService, immagine_url: e.target.value })}
        />
        <button onClick={addService}>Aggiungi Servizio</button>
      </section>
    </div>
  );
};

export default Servizi;
