import React from 'react';

const AboutUs = () => {
  return (
    <section style={styles.section}>
      <h2 style={styles.heading}>Chi Siamo</h2>
      <p style={styles.text}>
        La Beauty Armony Academy, fondata da Roberta Caruso a Melito di Porto Salvo, Ã¨ un centro di formazione all'avanguardia nel campo dell'estetica e dei trattamenti di bellezza. Offriamo anche corsi pratici e teorici, pensati per chi desidera intraprendere una carriera professionale nell'estetica.
      </p>
      <div style={styles.imgContainer}>
        <img src="https://yourimageurl.com/academy-image.jpg" alt="Beauty Armony Academy" style={styles.img} />
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #f8b400, #ff5722)',
    color: '#fff',
    borderRadius: '10px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: '1.5rem',
    letterSpacing: '1px',
  },
  text: {
    fontSize: '1.2rem',
    maxWidth: '800px',
    margin: '0 auto',
    marginBottom: '2rem',
    lineHeight: '1.6',
    color: '#fff',
  },
  imgContainer: {
    maxWidth: '600px',
    margin: '0 auto',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
    borderRadius: '10px',
    overflow: 'hidden',
  },
  img: {
    width: '100%',
    height: 'auto',
    borderRadius: '10px',
  },
};

export default AboutUs;
