import React, { useState, useEffect } from 'react';
import './GestioneServizi.css';

const PaginaHost = () => {
  const isHost = localStorage.getItem('isHost');
  const [servizi, setServizi] = useState([]);
  const [newService, setNewService] = useState({
    titolo: '',
    descrizione: '',
    immagine_url: '',
  });

  if (!isHost) {
    window.location.href = '/host-login'; // Reindirizza alla pagina di login se l'utente non è autenticato
  }

  // Fetch all services
  useEffect(() => {
    const fetchServizi = async () => {
      const response = await fetch('http://localhost:5000/api/servizi');
      const data = await response.json();
      setServizi(data);
    };
    fetchServizi();
  }, []);

  // Gestisce l'invio del form per aggiungere un nuovo servizio
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/servizi', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newService),
    });

    if (response.ok) {
      alert('Servizio aggiunto con successo');
      setNewService({ titolo: '', descrizione: '', immagine_url: '' });
      const updatedServizi = await response.json();
      setServizi([...servizi, updatedServizi]); // Aggiungi il nuovo servizio alla lista
    } else {
      alert('Errore nell\'aggiunta del servizio');
    }
  };

  // Gestisce la modifica di un servizio
  const handleEdit = async (id, updatedService) => {
    const response = await fetch(`http://localhost:5000/api/servizi/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedService),
    });

    if (response.ok) {
      alert('Servizio aggiornato con successo');
      const updatedServizi = servizi.map(servizio =>
        servizio.id === id ? { ...servizio, ...updatedService } : servizio
      );
      setServizi(updatedServizi);
    } else {
      alert('Errore nella modifica del servizio');
    }
  };

  // Gestisce l'eliminazione di un servizio
  const handleDelete = async (id) => {
    const response = await fetch(`http://localhost:5000/api/servizi/${id}`, {
      method: 'DELETE',
    });

    if (response.ok) {
      alert('Servizio eliminato con successo');
      setServizi(servizi.filter(servizio => servizio.id !== id));
    } else {
      alert('Errore nell\'eliminazione del servizio');
    }
  };

  return (
    <div className="pagina-host">
      <h1>Gestisci i Servizi</h1>

      {/* Form per aggiungere un nuovo servizio */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Titolo"
          value={newService.titolo}
          onChange={(e) => setNewService({ ...newService, titolo: e.target.value })}
        />
        <input
          type="text"
          placeholder="Descrizione"
          value={newService.descrizione}
          onChange={(e) => setNewService({ ...newService, descrizione: e.target.value })}
        />
        <input
          type="text"
          placeholder="Immagine URL"
          value={newService.immagine_url}
          onChange={(e) => setNewService({ ...newService, immagine_url: e.target.value })}
        />
        <button type="submit">Aggiungi Servizio</button>
      </form>

      {/* Elenco dei servizi esistenti */}
      <h2>Servizi Esistenti</h2>
      <ul>
        {servizi.map(servizio => (
          <li key={servizio.id}>
            <h3>{servizio.titolo}</h3>
            <p>{servizio.descrizione}</p>
            <img src={servizio.immagine_url} alt={servizio.titolo} />
            <button onClick={() => handleEdit(servizio.id, { titolo: 'Nuovo Titolo', descrizione: 'Nuova Descrizione', immagine_url: 'Nuova URL Immagine' })}>
              Modifica
            </button>
            <button onClick={() => handleDelete(servizio.id)}>Elimina</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PaginaHost;
