import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const HostLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Credenziali predefinite (modifica queste con le tue credenziali)
  const defaultUsername = 'admin';
  const defaultPassword = '12345';

  // Funzione di login
  const handleLogin = (e) => {
    e.preventDefault();

    // Verifica delle credenziali
    if (username === defaultUsername && password === defaultPassword) {
      // Se il login è corretto, salva l'autenticazione e reindirizza
      localStorage.setItem('isHost', true);  // Salviamo lo stato dell'host
      navigate('/pagina-host');  // Reindirizza alla pagina Host
    } else {
      alert('Credenziali errate. Riprova.');
    }
  };

  return (
    <div className="host-login">
      <h2>Login Host</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Accedi</button>
      </form>
    </div>
  );
};

export default HostLogin;
