// src/pages/Contatti.js
import React, { useState } from 'react';
import './Contatti.css';
const express = require('express');
const router = express.Router();

// Definisci una rotta di esempio
router.post('/invia-messaggio', (req, res) => {
    // La logica per inviare un messaggio o fare qualsiasi cosa
    res.send('Messaggio inviato!');
});

module.exports = router; // Assicurati che stai esportando il router


const Contatti = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    messaggio: '',
  });
  const [successo, setSuccesso] = useState('');
  const [errore, setErrore] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.nome || !formData.email || !formData.messaggio) {
      setErrore('Tutti i campi sono obbligatori!');
      return;
    }

    console.log('Invio richiesta', formData);

    fetch('http://localhost:5000/api/invia-messaggio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
    .then((response) => {
      console.log(response); // Log della risposta
      if (response.ok) {
          setSuccesso('Messaggio inviato con successo!');
          setErrore('');
          setFormData({ nome: '', email: '', messaggio: '' });
      } else {
          throw new Error('Errore nell\'invio del messaggio');
      }
  })  
      .catch((err) => {
        setErrore(`Si Ã¨ verificato un errore: ${err.message}`);
        setSuccesso('');
        console.log(err);
    });    
  };


  return (
    <div className="contatti-page">
      <section className="contatti-section">
        <h1>Contattaci</h1>
        <p>Hai domande o richieste? Siamo qui per aiutarti!</p>

        <div className="contatti-form">
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <label htmlFor="nome">Nome</label>
              <input
                type="text"
                id="nome"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="messaggio">Messaggio</label>
              <textarea
                id="messaggio"
                name="messaggio"
                value={formData.messaggio}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="btn invia">Invia Messaggio</button>
          </form>
        </div>

        <div className="contatti-info">
          <h2>Informazioni di contatto</h2>
          <p><strong>Indirizzo:</strong> Via Esempio 123, Melito di Porto Salvo</p>
          <p><strong>Email:</strong> info@beautyarmony.com</p>
          <p><strong>Telefono:</strong> +39 123 456 789</p>
        </div>
      </section>
    </div>
  );
};

export default Contatti;
