import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './GestioneRecensioni.css';

function GestisciRecensioni() {
  const [recensioniInAttesa, setRecensioniInAttesa] = useState([]);

  // Carica le recensioni in attesa di approvazione
  useEffect(() => {
    // Recupera le recensioni in attesa dal backend
    axios.get('http://localhost:5000/api/recensioni-in-attesa', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('jwtToken')}`, // Assuming JWT token is saved in localStorage
      }
    })
      .then(response => {
        setRecensioniInAttesa(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni in attesa', error);
      });
  }, []);

  // Approva una recensione
  const approvaRecensione = (id) => {
    axios.put('http://localhost:5000/api/approva-recensione', { id }, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('jwtToken')}`,
      }
    })
      .then(response => {
        // Rimuovi la recensione approvata dalla lista
        setRecensioniInAttesa(recensioniInAttesa.filter(recensione => recensione.id !== id));
      })
      .catch(error => {
        console.error('Errore nell\'approvazione della recensione', error);
      });
  };

  return (
    <div>
      <h2>Gestisci Recensioni</h2>

      {/* Lista delle recensioni in attesa di approvazione */}
      <div className="recensioni-in-attesa">
        {recensioniInAttesa.length === 0 ? (
          <p>Non ci sono recensioni in attesa di approvazione.</p>
        ) : (
          <ul>
            {recensioniInAttesa.map((recensione) => (
              <li key={recensione.id}>
                <div className="recensione">
                  <p><strong>Nome Utente:</strong> {recensione.nome}</p>
                  <p><strong>Recensione:</strong> {recensione.recensione}</p>
                  <p><strong>Rating:</strong> {recensione.rating}</p>
                  <button onClick={() => approvaRecensione(recensione.id)}>Approva</button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default GestisciRecensioni;
