import React, { useEffect, useState } from 'react';
import './Servizi.css';  // Assicurati che il percorso sia corretto


const Servizi = () => {
  const [servizi, setServizi] = useState([]);

  // Recupera i servizi dal back-end
  useEffect(() => {
    const fetchServizi = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/servizi');
        if (!response.ok) {
          throw new Error('Errore nella risposta del server');
        }
        const data = await response.json();
        setServizi(data);
      } catch (error) {
        console.error('Errore nel recupero dei servizi:', error);
      }
    };
    fetchServizi();
  }, []);

  return (
    <div className="servizi-page">
      <h2>I Nostri Servizi</h2>
      <div className="servizi-list">
        {servizi.map((servizio) => (
          <div className="servizio-card" key={servizio.id}>
            <img src={servizio.immagine_url} alt={servizio.titolo} />
            <h3>{servizio.titolo}</h3>
            <p>{servizio.descrizione}</p>
            <p><strong>Prezzo:</strong> â‚¬{servizio.prezzo}</p>
            <p><strong>Durata:</strong> {servizio.durata} minuti</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Servizi;
