import React from 'react';
import './Corsi.css';

const courses = [
  {
    title: "Corso Base di Estetica",
    description: "Impara le basi dell'estetica e le tecniche fondamentali per offrire servizi professionali.",
    price: "300€",
    image: "path-to-image1.jpg",
  },
  {
    title: "Corso di Massaggio Relax",
    description: "Tecniche di massaggio rilassante per ridurre lo stress e migliorare la circolazione.",
    price: "250€",
    image: "path-to-image2.jpg",
  },
  {
    title: "Corso Avanzato di Make-Up",
    description: "Tecniche avanzate per trucco professionale, adatto a chi vuole specializzarsi nel make-up.",
    price: "400€",
    image: "path-to-image3.jpg",
  },
];

const Corsi = () => {
  return (
    <div className="corsi-page">
      <header>
        <h1>Corsi della Beauty Armony Academy</h1>
        <p>Scopri i nostri corsi di formazione professionale nel settore estetico e benessere.</p>
      </header>

      <div className="corsi-list">
        {courses.map((course, index) => (
          <div key={index} className="course-card">
            <img src={course.image} alt={course.title} className="course-image" />
            <h3>{course.title}</h3>
            <p>{course.description}</p>
            <p className="price">{course.price}</p>
            <button className="cta-btn">Iscriviti ora</button>
          </div>
        ))}
      </div>

      <footer>
        <p>&copy; 2024 Beauty Armony Academy</p>
      </footer>
    </div>
  );
};

export default Corsi;
