import React from 'react';

const AboutUs = () => {
  return (
    <section style={styles.section}>
      <h2 style={styles.heading}>Chi Siamo</h2>
      <p style={styles.text}>
        La Beauty Armony Academy, fondata da Roberta Caruso a Melito di Porto Salvo, Ã¨ un centro di formazione all'avanguardia nel campo dell'estetica e dei trattamenti di bellezza. Offriamo corsi pratici e teorici, pensati per chi desidera intraprendere una carriera professionale nell'estetica.
      </p>

      <div style={styles.imgContainer}>
        <div style={styles.imgItem}>
          <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/316535098_506437981506034_186322896851486171_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=833d8c&_nc_ohc=DTLEvbmuGSsQ7kNvgGYqoAX&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=A4iaVpiDoWWe_mbOKTyAJCJ&oh=00_AYDGKl_NxQsSA1uG3Ieu3RvKV4zDplGNtJNjRFl_2qB7tg&oe=675CF2D8" alt="Aula di formazione" style={styles.img} />
          <p style={styles.imgDescription}>Aula di formazione</p>
        </div>
        <div style={styles.imgItem}>
          <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/317249457_510694891080343_1575729848846349599_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=833d8c&_nc_ohc=nr0KP1MzTswQ7kNvgEqNVgP&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=AzW0h0Cvg7NQhEq7cLxYZ99&oh=00_AYD7esR2DK2st94N57q4gmpiNXpCaUjERl4EhxQQgezYoQ&oe=675D08C3" alt="Corso pratico" style={styles.img} />
          <p style={styles.imgDescription}>Corso pratico</p>
        </div>
        <div style={styles.imgItem}>
          <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/439438134_823328713150291_6407591134954436987_n.jpg?_nc_cat=102&ccb=1-7&_nc_sid=127cfc&_nc_ohc=e6VhduHDALwQ7kNvgH6t6m3&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=AgaNIT2RK4XfCjSo5iAshoE&oh=00_AYColyoeiPRPYrZM5bCMpvVvSKofAhu3sRGTy2tK2yfNng&oe=675D1912" alt="Trattamenti di bellezza" style={styles.img} />
          <p style={styles.imgDescription}>Trattamenti di bellezza</p>
        </div>
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    backgroundColor: '#f4f4f4',
  },
  heading: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#333',
    marginBottom: '20px',
  },
  text: {
    fontSize: '1.2rem',
    maxWidth: '800px',
    margin: '0 auto',
    marginBottom: '2rem',
    color: '#555',
  },
  imgContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '20px',
    justifyItems: 'center',
    marginTop: '40px',
  },
  imgItem: {
    position: 'relative',
    width: '100%',
    overflow: 'hidden',
    borderRadius: '8px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  img: {
    width: '100%',
    height: 'auto',
    transition: 'transform 0.3s ease',
  },
  imgDescription: {
    position: 'absolute',
    bottom: '10px',
    left: '10px',
    right: '10px',
    backgroundColor: 'rgba(0, 0, 0, 0.6)',
    color: 'white',
    padding: '5px',
    textAlign: 'center',
    fontSize: '1rem',
    borderRadius: '5px',
  },
};

export default AboutUs;
