import React, { useEffect, useState } from 'react';
import './Servizi.css';

const Servizi = () => {
  const [servizi, setServizi] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/servizi') // Aggiornato per corrispondere alla rotta
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setServizi(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Errore nel recupero dei servizi:', error);
        setError(error.message);
        setIsLoading(false);
      });
  }, []);

  if (isLoading) {
    return <p>Caricamento in corso...</p>;
  }

  if (error) {
    return <p>Errore nel caricamento dei servizi: {error}</p>;
  }

  return (
    <div className="servizi-page">
      <section id="servizi" className="servizi-section">
        <h2>I Nostri Servizi</h2>
        {servizi.length === 0 ? (
          <p>Nessun servizio disponibile al momento.</p>
        ) : (
          <div className="servizi-list">
            {servizi.map((servizio) => (
              <div className="servizio-card" key={servizio.id}>
                <img src={servizio.immagine_url} alt={servizio.titolo} />
                <h3>{servizio.titolo}</h3>
                <p>{servizio.descrizione}</p>
                <p><strong>Prezzo:</strong> €{servizio.prezzo}</p>
                <p><strong>Durata:</strong> {servizio.durata} minuti</p>
                <div className="servizio-buttons">
                  <button className="btn prenota">Prenota</button>
                  <button className="btn scopri">Scopri di più</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      <section className="cta-section">
        <h2>Non Aspettare!</h2>
        <p>Prenota il tuo trattamento oggi e vivi un'esperienza di relax e bellezza unica.</p>
        <a href="#prenotazioni" className="btn-primary">Prenota Ora</a>
      </section>
    </div>
  );
};

export default Servizi;
