import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './GestioneCorsi.css';

function GestioneCorsi() {
    const [corsi, setCorsi] = useState([]);
    const [newCorso, setNewCorso] = useState({
        titolo: '',
        descrizione: '',
        immagine_url: '',
        prezzo: '',
        durata: ''
    });
    const [editCorso, setEditCorso] = useState(null);

    // Fetch all courses
    useEffect(() => {
        axios.get('http://localhost:5000/api/corsi')
            .then(response => {
                setCorsi(response.data);
            })
            .catch(err => {
                console.error('Errore nel recupero dei corsi:', err);
            });
    }, []);

    // Aggiungi un nuovo corso
    const handleAddCourse = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5000/api/corsi', newCorso)
            .then(response => {
                setCorsi([...corsi, response.data]);
                setNewCorso({
                    titolo: '',
                    descrizione: '',
                    immagine_url: '',
                    prezzo: '',
                    durata: ''
                });
            })
            .catch(err => {
                console.error('Errore nell\'aggiunta del corso:', err);
            });
    };

    // Modifica un corso esistente
    const handleEdit = (corso) => {
        setEditCorso(corso);
    };

    const handleUpdateCourse = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:5000/api/corsi/${editCorso.id}`, editCorso)
            .then(response => {
                setCorsi(corsi.map(c => (c.id === editCorso.id ? editCorso : c)));
                setEditCorso(null);
            })
            .catch(err => {
                console.error('Errore nell\'aggiornamento del corso:', err);
            });
    };

    // Elimina un corso
    const handleDelete = (id) => {
        axios.delete(`http://localhost:5000/api/corsi/${id}`)
            .then(() => {
                setCorsi(corsi.filter(corso => corso.id !== id));
            })
            .catch(err => {
                console.error('Errore nell\'eliminazione del corso:', err);
            });
    };

    return (
        <div className="gestione-corsi">
            <h1>Gestione Corsi</h1>

            {/* Form per aggiungere un nuovo corso */}
            <h2>Aggiungi Nuovo Corso</h2>
            <form onSubmit={handleAddCourse}>
                <input
                    type="text"
                    placeholder="Titolo"
                    value={newCorso.titolo}
                    onChange={(e) => setNewCorso({ ...newCorso, titolo: e.target.value })}
                />
                <textarea
                    placeholder="Descrizione"
                    value={newCorso.descrizione}
                    onChange={(e) => setNewCorso({ ...newCorso, descrizione: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Immagine URL"
                    value={newCorso.immagine_url}
                    onChange={(e) => setNewCorso({ ...newCorso, immagine_url: e.target.value })}
                />
                <input
                    type="number"
                    placeholder="Prezzo"
                    value={newCorso.prezzo}
                    onChange={(e) => setNewCorso({ ...newCorso, prezzo: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Durata"
                    value={newCorso.durata}
                    onChange={(e) => setNewCorso({ ...newCorso, durata: e.target.value })}
                />
                <button type="submit">Aggiungi Corso</button>
            </form>

            {/* Elenco dei corsi esistenti */}
            <h2>Corsi Esistenti</h2>
            <div className="corsi-container">
                {corsi.map(corso => (
                    <div className="corso-card" key={corso.id}>
                        <img src={corso.immagine_url} alt={corso.titolo} className="corso-card-img" />
                        <div className="corso-card-content">
                            <h3>{corso.titolo}</h3>
                            <p>{corso.descrizione}</p>
                            <p><strong>Prezzo:</strong> â‚¬{corso.prezzo}</p>
                            <p><strong>Durata:</strong> {corso.durata}</p>
                        </div>
                        <div className="corso-card-actions">
                            <button onClick={() => handleEdit(corso)}>Modifica</button>
                            <button onClick={() => handleDelete(corso.id)}>Elimina</button>
                        </div>
                    </div>
                ))}
            </div>

            {/* Modifica corso */}
            {editCorso && (
                <div className="edit-corso-form">
                    <h2>Modifica Corso</h2>
                    <form onSubmit={handleUpdateCourse}>
                        <input
                            type="text"
                            value={editCorso.titolo}
                            onChange={(e) => setEditCorso({ ...editCorso, titolo: e.target.value })}
                        />
                        <textarea
                            value={editCorso.descrizione}
                            onChange={(e) => setEditCorso({ ...editCorso, descrizione: e.target.value })}
                        />
                        <input
                            type="text"
                            value={editCorso.immagine_url}
                            onChange={(e) => setEditCorso({ ...editCorso, immagine_url: e.target.value })}
                        />
                        <input
                            type="number"
                            value={editCorso.prezzo}
                            onChange={(e) => setEditCorso({ ...editCorso, prezzo: e.target.value })}
                        />
                        <input
                            type="text"
                            value={editCorso.durata}
                            onChange={(e) => setEditCorso({ ...editCorso, durata: e.target.value })}
                        />
                        <button type="submit">Aggiorna Corso</button>
                        <button type="button" onClick={() => setEditCorso(null)}>Annulla</button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default GestioneCorsi;
