import React, { useEffect, useState } from 'react';

const Recensioni = () => {
  const [recensioni, setRecensioni] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/recensioni')
      .then((response) => response.json())
      .then((data) => setRecensioni(data))
      .catch((error) => console.error('Errore nel recuperare le recensioni:', error));
  }, []);

  return (
    <div>
      <h1>Recensioni dei nostri clienti</h1>
      {recensioni.length > 0 ? (
        <ul>
          {recensioni.map((recensione) => (
            <li key={recensione.id}>
              <h3>{recensione.nome_cliente}</h3>
              <p>{recensione.recensione}</p>
              <p><strong>Data:</strong> {new Date(recensione.data).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>Non ci sono recensioni disponibili.</p>
      )}
    </div>
  );
};

export default Recensioni;
