import React, { useState, useEffect } from 'react';
import './GestioneServizi.css';

const PaginaHost = () => {
  const isHost = localStorage.getItem('isHost') === 'true';

  // Reindirizzamento se l'utente non è autenticato
  if (!isHost) {
    window.location.href = '/host-login';
  }

  const [servizi, setServizi] = useState([]);
  const [editingService, setEditingService] = useState(null);
  const [newService, setNewService] = useState({
    titolo: '',
    descrizione: '',
    prezzo: '',
    durata: '',
    immagine_url: '',
  });

  // Recupera i servizi dal server
  useEffect(() => {
    const fetchServizi = async () => {
      const response = await fetch('http://localhost:5000/api/servizi');
      const data = await response.json();
      setServizi(data);
    };
    fetchServizi();
  }, []);

  // Gestione aggiunta servizio
  const handleAdd = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/servizi', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newService),
    });

    if (response.ok) {
      alert('Servizio aggiunto con successo');
      setNewService({ titolo: '', descrizione: '', prezzo: '', durata: '', immagine_url: '' });
      const updatedServizi = await fetch('http://localhost:5000/api/servizi').then((res) => res.json());
      setServizi(updatedServizi);
    } else {
      alert('Errore nell\'aggiunta del servizio');
    }
  };

  // Gestione eliminazione servizio
  const handleDelete = async (id) => {
    if (window.confirm('Sei sicuro di voler eliminare questo servizio?')) {
      const response = await fetch(`http://localhost:5000/api/servizi/${id}`, { method: 'DELETE' });
      if (response.ok) {
        alert('Servizio eliminato con successo');
        setServizi(servizi.filter((servizio) => servizio.id !== id));
      } else {
        alert('Errore durante l\'eliminazione del servizio');
      }
    }
  };

  // Gestione modifica servizio
  const handleEdit = (servizio) => {
    setEditingService(servizio);
    setNewService(servizio);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const response = await fetch(`http://localhost:5000/api/servizi/${editingService.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newService),
    });

    if (response.ok) {
      alert('Servizio aggiornato con successo');
      const updatedServizi = await fetch('http://localhost:5000/api/servizi').then((res) => res.json());
      setServizi(updatedServizi);
      setEditingService(null);
      setNewService({ titolo: '', descrizione: '', prezzo: '', durata: '', immagine_url: '' });
    } else {
      alert('Errore durante l\'aggiornamento del servizio');
    }
  };

  return (
    <div className="pagina-host">
      <h1>Gestione Servizi</h1>

      {/* Elenco dei Servizi */}
      <div className="elenco-servizi">
        {servizi.map((servizio) => (
          <div key={servizio.id} className="servizio">
            <h2>{servizio.titolo}</h2>
            <p>{servizio.descrizione}</p>
            <p>Prezzo: €{servizio.prezzo}</p>
            <p>Durata: {servizio.durata} ore</p>
            <img src={servizio.immagine_url} alt={servizio.titolo} />
            <button onClick={() => handleEdit(servizio)}>Modifica</button>
            <button onClick={() => handleDelete(servizio.id)}>Elimina</button>
          </div>
        ))}
      </div>

      {/* Form per Aggiunta/Modifica */}
      <form onSubmit={editingService ? handleUpdate : handleAdd}>
        <h2>{editingService ? 'Modifica Servizio' : 'Aggiungi un Servizio'}</h2>
        <input
          type="text"
          placeholder="Titolo"
          value={newService.titolo}
          onChange={(e) => setNewService({ ...newService, titolo: e.target.value })}
        />
        <input
          type="text"
          placeholder="Descrizione"
          value={newService.descrizione}
          onChange={(e) => setNewService({ ...newService, descrizione: e.target.value })}
        />
        <input
          type="number"
          placeholder="Prezzo"
          value={newService.prezzo}
          onChange={(e) => setNewService({ ...newService, prezzo: e.target.value })}
        />
        <input
          type="number"
          placeholder="Durata"
          value={newService.durata}
          onChange={(e) => setNewService({ ...newService, durata: e.target.value })}
        />
        <input
          type="text"
          placeholder="Immagine URL"
          value={newService.immagine_url}
          onChange={(e) => setNewService({ ...newService, immagine_url: e.target.value })}
        />
        <button type="submit">{editingService ? 'Aggiorna' : 'Aggiungi'}</button>
        {editingService && <button onClick={() => setEditingService(null)}>Annulla</button>}
      </form>
    </div>
  );
};

export default PaginaHost;
