import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GestisciRecensioni() {
  const [recensioni, setRecensioni] = useState([]);

  // Carica le recensioni in attesa di approvazione
  useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni-in-attesa')
      .then(response => {
        setRecensioni(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni', error);
      });
  }, []);

  // Funzione per approvare una recensione
  const approvaRecensione = (id) => {
    axios.put('http://localhost:5000/api/approva-recensione', { id })
      .then(() => {
        setRecensioni(recensioni.filter(rec => rec.id !== id)); // Rimuove la recensione approvata
      })
      .catch(error => {
        console.error('Errore nell\'approvazione della recensione', error);
      });
  };

  return (
    <div>
      <h2>Gestione Recensioni</h2>
      <ul>
        {recensioni.map((recensione) => (
          <li key={recensione.id}>
            <p><strong>{recensione.nome}</strong>: {recensione.recensione}</p>
            <button onClick={() => approvaRecensione(recensione.id)}>Approva</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GestisciRecensioni;
