import React, { useState } from 'react';
import axios from 'axios';

function AggiungiRecensione() {
    const [recensione, setRecensione] = useState('');
    const [userId, setUserId] = useState(1); // Ad esempio, l'ID dell'utente che sta aggiungendo la recensione
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:5000/api/recensioni', {
                userId,
                recensione
            });
            setSuccessMessage(response.data.message);
            setError('');
        } catch (err) {
            setError(err.response.data.message);
            setSuccessMessage('');
        }
    };

    return (
        <div>
            <h2>Aggiungi la tua recensione</h2>
            <form onSubmit={handleSubmit}>
                <textarea
                    value={recensione}
                    onChange={(e) => setRecensione(e.target.value)}
                    placeholder="Scrivi la tua recensione"
                    rows="4"
                    cols="50"
                />
                <button type="submit">Invia recensione</button>
            </form>

            {error && <p style={{ color: 'red' }}>{error}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
        </div>
    );
}

export default AggiungiRecensione;
