import React from 'react';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div style={styles.footerContent}>
        <p>&copy; 2024 Beauty Armony Academy | All Rights Reserved</p>
        <div style={styles.socialLinks}>
          <a href="./instagram.svg" style={styles.socialIcon} target="_blank" rel="noopener noreferrer">
            <img src="/path/to/instagram-icon.png" alt="Instagram" style={styles.icon} />
          </a>
          <a href="https://www.tiktok.com" style={styles.socialIcon} target="_blank" rel="noopener noreferrer">
            <img src="/path/to/tiktok-icon.png" alt="TikTok" style={styles.icon} />
          </a>
        </div>
        <div style={styles.legalInfo}>
          <a href="/privacy-policy" style={styles.legalLink}>Privacy Policy</a>
          <span> | </span>
          <a href="/terms-of-service" style={styles.legalLink}>Terms of Service</a>
        </div>
      </div>
    </footer>
  );
};

const styles = {
  footer: {
    background: 'linear-gradient(135deg, #f7b42c, #fc575e)', // Gradiente caldo
    color: '#fff',
    textAlign: 'center',
    padding: '2rem',
    position: 'relative',
    width: '100%',
    bottom: 0,
    borderTopLeftRadius: '15px',
    borderTopRightRadius: '15px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  footerContent: {
    maxWidth: '1000px',
    margin: '0 auto',
  },
  socialLinks: {
    marginTop: '10px',
  },
  socialIcon: {
    margin: '0 15px',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  legalInfo: {
    marginTop: '20px',
  },
  legalLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '14px',
  },
};

export default Footer;
