import React from 'react';
import { FaInstagram, FaTiktok } from 'react-icons/fa';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div style={styles.socialIcons}>
        <a 
          href="https://www.instagram.com/beautyarmony/" 
          target="_blank" 
          rel="noopener noreferrer" 
          style={styles.iconLink}
        >
          <FaInstagram size={30} color="#fff" />
        </a>
        <a 
          href="https://www.tiktok.com/@beautyarmony" 
          target="_blank" 
          rel="noopener noreferrer" 
          style={styles.iconLink}
        >
          <FaTiktok size={30} color="#fff" />
        </a>
      </div>
      <div style={styles.footerText}>
        <p style={styles.copyright}>© 2024 Beauty Armony Academy | All Rights Reserved</p>
        <p>
          <a 
            href="/privacy-policy" 
            style={styles.legalLinks}
          >
            Privacy Policy
          </a> | 
          <a 
            href="/terms-and-conditions" 
            style={styles.legalLinks}
          >
            Terms & Conditions
          </a>
        </p>
      </div>
    </footer>
  );
};

const styles = {
  footer: {
    background: '#333', // Colore di sfondo scuro
    color: '#fff', // Testo bianco per contrasto
    textAlign: 'center',
    padding: '30px 0',
    position: 'relative',
    width: '100%',
    bottom: 0,
    display: 'flex',
    flexDirection: 'column', // Disporre gli elementi in colonna
    alignItems: 'center', // Centrare il contenuto
    fontFamily: 'Arial, sans-serif', // Font coerente con il resto del sito
  },
  socialIcons: {
    display: 'flex',
    justifyContent: 'center', // Centra le icone orizzontalmente
    gap: '20px', // Distanza tra le icone
    marginTop: '20px', // Aggiungi spazio tra il testo e le icone
  },
  iconLink: {
    textDecoration: 'none',
    transition: 'transform 0.3s ease, filter 0.3s ease',
  },
  footerText: {
    fontSize: '16px', // Dimensione del font per il testo del footer
  },
  copyright: {
    fontSize: '14px',
    marginBottom: '10px',
    fontWeight: '300', // Font leggermente più sottile
  },
  legalLinks: {
    color: '#fff',
    textDecoration: 'none',
    margin: '0 10px',
    fontSize: '14px',
    fontWeight: '400', // Font normale per i link legali
    transition: 'color 0.3s ease', // Transizione per il colore dei link
  },
  legalLinksHover: {
    color: '#f8b400', // Colore arancione quando il link è in hover
  },
  iconHover: {
    transform: 'scale(1.2)',
    filter: 'brightness(1.2)', // Aggiunta luminosità all'hover
  },
};

export default Footer;
