import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Usato per il reindirizzamento
import './Login.css';

function Login() {
  const [userType, setUserType] = useState(""); // Stato per gestire la selezione
  const navigate = useNavigate();  // Usato per il reindirizzamento

  // Funzione per gestire la selezione dell'utente
  const handleLogin = () => {
    if (userType === "cliente") {
      // Se l'utente è cliente, vai alla home page (o qualsiasi pagina cliente)
      navigate("/home");
    } else if (userType === "admin") {
      // Se l'utente è admin, vai alla pagina di gestione recensioni
      navigate("/gestisci-recensioni");
    } else {
      alert("Seleziona una tipologia di utente!");
    }
  };

  return (
    <div className="login-container">
      <h2>Seleziona il tuo tipo di utente</h2>
      <div>
        <button onClick={() => setUserType("cliente")}>Cliente</button>
        <button onClick={() => setUserType("admin")}>Admin</button>
      </div>
      <button onClick={handleLogin}>Accedi</button>
    </div>
  );
}

export default Login;
