import React from 'react';

const Hero = () => {
  return (
    <div style={styles.hero}>
      <h1>Benvenuti alla Beauty Armony Academy</h1>
      <p>Impara le migliori tecniche estetiche con i nostri corsi professionali.</p>
    </div>
  );
};

const styles = {
  hero: {
    backgroundColor: '#f8b400',
    padding: '2rem',
    textAlign: 'center',
    color: '#fff',
  },
};

export default Hero;
