import React from 'react';
import Slider from 'react-slick';

const AboutUs = () => {
  const settings = {
    dots: false, // Rimuove i puntini di navigazione
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Attiva l'autoplay
    autoplaySpeed: 3000, // Imposta il tempo tra ogni slide
    fade: true, // Transizione di dissolvenza tra le slide
    arrows: false, // Disabilita le frecce
  };

  return (
    <section style={styles.section}>
      <h2 style={styles.heading}>Chi Siamo</h2>
      <p style={styles.text}>
        La Beauty Armony Academy, fondata da Roberta Caruso a Melito di Porto Salvo, Ã¨ un centro di formazione all'avanguardia nel campo dell'estetica e dei trattamenti di bellezza. Offriamo corsi pratici e teorici, pensati per chi desidera intraprendere una carriera professionale nell'estetica.
      </p>
      <div style={styles.imgContainer}>
        <Slider {...settings}>
          <div style={styles.slide}>
            <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/449612161_865249402291555_1056623531847696087_n.jpg?_nc_cat=108&ccb=1-7&_nc_sid=833d8c&_nc_ohc=bXMwXL7RrfYQ7kNvgGC3_X8&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=AoazsCAFBB3MYWA4j3R3K0c&oh=00_AYAZebdb9NlfqzdS76FRdN75nlvSqhbR7QzzxXuAJsDUlg&oe=675CF331" alt="Beauty Armony Academy 1" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://scontent.fbri2-1.fna.fbcdn.net/v/t39.30808-6/453655442_883007193849109_8280932997284654439_n.jpg?_nc_cat=110&ccb=1-7&_nc_sid=833d8c&_nc_ohc=iS8POX_oHKIQ7kNvgG2DX6V&_nc_zt=23&_nc_ht=scontent.fbri2-1.fna&_nc_gid=AVphwkrbXytSHSdjoQKekUJ&oh=00_AYAfo4MPkru24Q6N8PWjeVM7kt4gq1-zbA3bvJUbmKJ8nA&oe=675CE500" alt="Beauty Armony Academy 2" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://yourimageurl.com/academy-image3.jpg" alt="Beauty Armony Academy 3" style={styles.img} />
          </div>
        </Slider>
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #f8b400, #ff5722)',
    color: '#fff',
    borderRadius: '10px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: '1.5rem',
    letterSpacing: '1px',
  },
  text: {
    fontSize: '1.2rem',
    maxWidth: '800px',
    margin: '0 auto',
    marginBottom: '2rem',
    lineHeight: '1.6',
    color: '#fff',
  },
  imgContainer: {
    maxWidth: '800px',
    margin: '0 auto',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.2)',
  },
  slide: {
    position: 'relative',
  },
  img: {
    width: '100%', // Le immagini si adattano alla larghezza del container
    height: 'auto', // Altezza automatica in base alla larghezza per mantenere le proporzioni
    objectFit: 'cover', // Le immagini coprono completamente l'area senza distorsioni
    borderRadius: '10px',
  },
};

export default AboutUs;
