import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import AboutUs from './components/AboutUs';
import Corsi from './pages/Corsi';  // Importa la pagina Corsi
import Testimonials from './components/Testimonials'; // Importa Testimonials
import Footer from './components/Footer';
import Servizi from './pages/Servizi';  // Importa la pagina dei servizi
import Contatti from './pages/Contatti';  // Importa la pagina dei contatti
import Recensioni from './pages/Recensioni';  // Importa la pagina delle recensioni
import GestioneRecensioni from './pages/GestioneRecensioni';
import './App.css';

function App() {
  const isAdmin = false; // Usa una logica reale per verificare il ruolo dell'utente
  return (
    <Router>
      <Navbar />
      <main>
        <Routes>
          {/* Pagina principale con Hero, AboutUs, e Testimonials */}
          <Route path="/" element={<><Hero /><AboutUs /><Testimonials /></>} />

          {/* Altri percorsi */}
          <Route path="/corsi" element={<Corsi />} />
          <Route path="/servizi" element={<Servizi />} />
          <Route path="/contatti" element={<Contatti />} />
          <Route path="/recensioni" element={<Recensioni />} />
          <Route path="/host-login" element={<HostLogin />} />

          {/* Protezione per la pagina Host */}
          <Route
            path="/pagina-host"
            element={isHost ? <PaginaHost /> : <HostLogin />}
          />

          {/* Pagina di gestione recensioni visibile solo se isAdmin Ã¨ true */}
          {isAdmin && <Route path="/GestioneRecensioni" element={<GestioneRecensioni />} />}
        </Routes>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
