import React from 'react';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div style={styles.footerText}>
        <p>&copy; 2024 Beauty Armony Academy | All Rights Reserved</p>
        <p>
          <a 
            href="/privacy-policy" 
            style={styles.legalLinks}
          >
            Privacy Policy
          </a> | 
          <a 
            href="/terms-and-conditions" 
            style={styles.legalLinks}
          >
            Terms & Conditions
          </a>
        </p>
      </div>

      <div style={styles.socialIcons}>
        {/* Instagram Icon */}
        <a 
          href="https://www.instagram.com/beautyarmony/" 
          target="_blank" 
          rel="noopener noreferrer" 
          style={styles.iconLink}
        >
          <img src="path_to_your_instagram_icon.svg" alt="Instagram" style={styles.icon} />
        </a>

        {/* TikTok Icon */}
        <a 
          href="https://www.tiktok.com/@beautyarmony" 
          target="_blank" 
          rel="noopener noreferrer" 
          style={styles.iconLink}
        >
          <img src="path_to_your_tiktok_icon.svg" alt="TikTok" style={styles.icon} />
        </a>
      </div>
    </footer>
  );
};

const styles = {
  footer: {
    background: 'linear-gradient(135deg, #f8b400, #f05d23)', // Gradiente arancione dorato
    color: '#fff',
    textAlign: 'center',
    padding: '30px 0',
    position: 'relative',
    width: '100%',
    bottom: 0,
  },
  footerText: {
    marginBottom: '20px',
  },
  legalLinks: {
    color: '#fff',
    textDecoration: 'none',
    margin: '0 5px',
    transition: 'color 0.3s',
  },
  legalLinksHover: {
    color: '#333',
  },
  socialIcons: {
    display: 'flex',
    justifyContent: 'center',
    gap: '30px',
    marginTop: '20px',
  },
  iconLink: {
    textDecoration: 'none',
    transition: 'transform 0.2s',
  },
  icon: {
    width: '40px',
    height: '40px',
    transition: 'transform 0.3s, filter 0.3s',
  },
  iconHover: {
    transform: 'scale(1.1)',
    filter: 'brightness(1.2)',
  },
};

export default Footer;
