import React from 'react';

const Hero = () => {
  return (
    <div style={styles.hero}>
      <div style={styles.textContainer}>
        <h1 style={styles.heading}>Benvenuti alla Beauty Armony Academy</h1>
        <p style={styles.subheading}>Scopri il nostro programma di corsi professionali per l'estetica e i trattamenti di bellezza.</p>
        <button style={styles.button}>Scopri i Corsi</button>
      </div>
    </div>
  );
};

const styles = {
  hero: {
    background: 'url("https://yourimageurl.com") no-repeat center center',
    backgroundSize: 'cover',
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff',
    textAlign: 'center',
  },
  textContainer: {
    maxWidth: '600px',
    padding: '2rem',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  heading: {
    fontSize: '3rem',
    fontWeight: 'bold',
  },
  subheading: {
    fontSize: '1.2rem',
    marginTop: '1rem',
  },
  button: {
    marginTop: '2rem',
    padding: '0.8rem 1.5rem',
    backgroundColor: '#f8b400',
    color: '#fff',
    border: 'none',
    fontSize: '1rem',
    cursor: 'pointer',
    borderRadius: '5px',
  },
};

export default Hero;
