import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recensioni = () => {
  const [recensioni, setRecensioni] = useState([]);

  useEffect(() => {
    // Chiamata per recuperare le recensioni dal backend
    axios.get('http://localhost:5000/api/recensioni')
      .then((response) => {
        setRecensioni(response.data);  // Imposta i dati delle recensioni nello stato
      })
      .catch((error) => {
        console.error('Errore nel recupero delle recensioni:', error);
      });
  }, []);

  return (
    <div>
      <h2>Recensioni</h2>
      <ul>
        {recensioni.map((recensione) => (
          <li key={recensione.id}>
            <strong>{recensione.nome_cliente}</strong>
            <p>{recensione.recensione}</p>
            <p>Valutazione: {recensione.valutazione} / 5</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Recensioni;
