import React, { useState } from 'react';
import axios from 'axios';
import './Contatti.css';

function Contatti() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [messaggio, setMessaggio] = useState('');
  const [errore, setErrore] = useState('');
  const [successo, setSuccesso] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Verifica che i campi non siano vuoti
    if (!nome || !email || !messaggio) {
      setErrore('Tutti i campi sono obbligatori');
      return;
    }

    // Invia i dati del form al server
    axios
      .post('http://localhost:5000/api/invia-messaggio', { nome, email, messaggio })
      .then((response) => {
        setSuccesso('Messaggio inviato con successo!');
        setNome('');
        setEmail('');
        setMessaggio('');
        setErrore('');
      })
      .catch((err) => {
        setErrore('Si è verificato un errore. Riprova.');
        setSuccesso('');
      });
  };

  return (
    <div className="contattaci">
      <h2>Contattaci</h2>
      {errore && <p className="errore">{errore}</p>}
      {successo && <p className="successo">{successo}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Il tuo nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <input
          type="email"
          placeholder="La tua email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <textarea
          placeholder="Il tuo messaggio"
          value={messaggio}
          onChange={(e) => setMessaggio(e.target.value)}
        ></textarea>
        <button type="submit">Invia Messaggio</button>
      </form>
    </div>
  );
}

export default Contatti;
