import React, { useState } from 'react';
import './Contatti.css';

const Contatti = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    messaggio: '',
  });

  const [successo, setSuccesso] = useState('');
  const [errore, setErrore] = useState('');

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if all fields are filled
    if (!formData.nome || !formData.email || !formData.messaggio) {
      setErrore('Tutti i campi sono obbligatori!');
      return;
    }

    // Send form data to server
    fetch('http://localhost:5000/api/invia-messaggio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message) {
          setSuccesso('Messaggio inviato con successo!');
          setErrore('');
        }
      })
      .catch((err) => {
        console.error('Error:', err);
        setErrore('Si è verificato un errore. Riprova più tardi.');
      });
  };

  return (
    <div className="contatti-page">
      <section className="contatti-section">
        <h1>Contattaci</h1>
        <p>Hai domande o richieste? Siamo qui per aiutarti!</p>

        <div className="contatti-form">
          <form onSubmit={handleSubmit}>
            <fieldset>
              <legend>Compila il modulo per contattarci</legend>

              <div className="input-group">
                <label htmlFor="nome">Nome</label>
                <input
                  type="text"
                  id="nome"
                  name="nome"
                  value={formData.nome}
                  onChange={handleChange}
                  placeholder="Inserisci il tuo nome"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Inserisci la tua email"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor="messaggio">Messaggio</label>
                <textarea
                  id="messaggio"
                  name="messaggio"
                  value={formData.messaggio}
                  onChange={handleChange}
                  placeholder="Scrivi qui il tuo messaggio"
                  required
                />
              </div>

              <button type="submit" className="btn invia">
                Invia Messaggio
              </button>
            </fieldset>
          </form>

          {errore && <div className="errore">{errore}</div>}
          {successo && <div className="successo">{successo}</div>}
        </div>

        <div className="contatti-info">
          <h2>Informazioni di contatto</h2>
          <p><i className="fas fa-map-marker-alt"></i> <strong>Indirizzo:</strong> Via Antonio Gramsci n. 36, Melito di Porto Salvo</p>
          <p><i className="fas fa-envelope"></i> <strong>Email:</strong> beautyarmonyacademy@gmail.com</p>
          <p><i className="fas fa-phone"></i> <strong>Telefono:</strong> +39 366 369 1564</p>
        </div>
      </section>
    </div>
  );
};

export default Contatti;
