import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Corsi.css';

function Corsi() {
    const [corsi, setCorsi] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('http://localhost:5000/api/corsi')
            .then(response => {
                setCorsi(response.data);
                setLoading(false);
            })
            .catch(err => {
                setError('Errore nel recupero dei corsi');
                setLoading(false);
                console.error(err);
            });
    }, []);

    return (
        <div className="corsi-page">
            <h2>I nostri corsi</h2>
            {loading && <div className="loading-message">Caricamento in corso...</div>}
            {error && <p className="error-message">{error}</p>}
            
            <div className="corsi-container">
                {corsi.map(corso => (
                    <div className="corso-card" key={corso.id}>
                        <img src={corso.immagine_url} alt={corso.titolo} className="card-image" />
                        <div className="card-content">
                            <h3>{corso.titolo}</h3>
                            <p>{corso.descrizione}</p>
                            <p><strong>Prezzo:</strong> â‚¬{corso.prezzo}</p>
                            <p><strong>Durata:</strong> {corso.durata}</p>
                            
                            {/* Bottone che apre WhatsApp con il messaggio precompilato */}
                            <a
                              href={`https://wa.me/+393663691564?text=Richiedo%20informazioni%20sul%20corso%20Estetica%20Avanzata`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="btn-iscriviti"
                            >
                              Iscriviti Ora
                            </a>


                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Corsi;
