import React from 'react';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <p>&copy; 2024 Beauty Armony Academy | All Rights Reserved</p>
    </footer>
  );
};

const styles = {
  footer: {
    background: 'linear-gradient(135deg, #f7b42c, #fc575e)', // Gradiente caldo
    color: '#fff',
    textAlign: 'center',
    padding: '2rem',
    position: 'relative',
    width: '100%',
    bottom: 0,
    borderTopLeftRadius: '15px',
    borderTopRightRadius: '15px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', // Ombra leggera per profondità
  },
};

export default Footer;
