import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Recensioni = () => {
  const [recensioni, setRecensioni] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni')
      .then(response => {
        setRecensioni(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni', error);
      });
  }, []);

  return (
    <div>
      <h1>Le recensioni dei nostri clienti</h1>
      <div className="reviews">
        {recensioni.map((recensione) => (
          <div key={recensione.id}>
            <h3>{recensione.nome_cliente}</h3>
            <p>{recensione.recensione}</p>
            <p>Valutazione: {recensione.rating} stelle</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recensioni;
