import React from 'react';
import { FaInstagram, FaTiktok } from 'react-icons/fa';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div className="social-icons">
        <a href="https://www.instagram.com/beautyarmony/" target="_blank" rel="noopener noreferrer">
          <FaInstagram size={30} color="#fff" />
        </a>
        <a href="https://www.tiktok.com/@beautyarmony" target="_blank" rel="noopener noreferrer">
          <FaTiktok size={30} color="#fff" />
        </a>
      </div>
      <p>&copy; 2024 Beauty Armony Academy | All Rights Reserved</p>
    </footer>
  );
};

const styles = {
  footer: {
    backgroundColor: '#f8b400',
    color: '#fff',
    textAlign: 'center',
    padding: '1rem',
    position: 'relative',
    width: '100%',
    bottom: 0,
  },
  footerContent: {
    maxWidth: '1000px',
    margin: '0 auto',
  },
  socialLinks: {
    marginTop: '10px',
  },
  socialIcon: {
    margin: '0 15px',
  },
  icon: {
    width: '30px',
    height: '30px',
  },
  legalInfo: {
    marginTop: '20px',
  },
  legalLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '14px',
  },
};

export default Footer;
