import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // To handle redirection

function GestisciRecensioni() {
  const navigate = useNavigate();
  const [recensioniInAttesa, setRecensioniInAttesa] = useState([]);
  
  useEffect(() => {
    const token = localStorage.getItem('jwtToken'); // Get the JWT token from localStorage
    if (!token) {
      // Redirect to login if no token is found
      navigate('/login');
    } else {
      // Fetch reviews pending approval from the backend
      axios.get('http://localhost:5000/api/recensioni-in-attesa', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
      .then(response => {
        setRecensioniInAttesa(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni in attesa', error);
      });
    }
  }, [navigate]); // Effect runs on component mount or when navigate changes

  const approvaRecensione = (id) => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      axios.put('http://localhost:5000/api/approva-recensione', { id }, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
      .then(response => {
        setRecensioniInAttesa(prevState => prevState.filter(recensione => recensione.id !== id)); // Remove approved review
      })
      .catch(error => {
        console.error('Errore nell\'approvazione della recensione', error);
      });
    }
  };

  return (
    <div>
      <h2>Gestisci Recensioni</h2>

      <ul>
        {recensioniInAttesa.map((recensione) => (
          <li key={recensione.id}>
            <p><strong>Nome Utente:</strong> {recensione.nome}</p>
            <p><strong>Recensione:</strong> {recensione.recensione}</p>
            <p><strong>Rating:</strong> {recensione.rating}</p>
            <button onClick={() => approvaRecensione(recensione.id)}>Approva</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GestisciRecensioni;
