import React from 'react';

const Testimonials = () => {
  return (
    <section style={styles.section}>
      <h2>Cosa Dicono i Nostri Studenti</h2>
      <div style={styles.testimonials}>
        <div style={styles.testimonial}>
          <p style={styles.text}>"I corsi sono incredibili! Ho imparato tantissimo e ora sono pronta a lavorare nel campo dell'estetica!"</p>
          <p>- Maria, Studente</p>
        </div>
        <div style={styles.testimonial}>
          <p style={styles.text}>"L'insegnante Ã¨ molto preparata e i corsi sono molto pratici, perfetti per imparare sul campo!"</p>
          <p>- Laura, Ex Studente</p>
        </div>
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    backgroundColor: '#f4f4f4',
  },
  text: {
    fontStyle: 'italic',
    fontSize: '1.1rem',
  },
  testimonials: {
    display: 'flex',
    justifyContent: 'space-around',
    gap: '2rem',
    marginTop: '2rem',
  },
  testimonial: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '8px',
    width: '40%',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
};

export default Testimonials;
