import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import AboutUs from './components/AboutUs';
import Corsi from './pages/Corsi';
import Testimonials from './components/Testimonials';
import Footer from './components/Footer';
import Servizi from './pages/Servizi';
import Contatti from './pages/Contatti';
import Recensioni from './pages/Recensioni';
import GestioneRecensioni from './pages/GestioneRecensioni';
import HostLogin from './pages/HostLogin';
import './App.css';

function App() {
  const [isHost, setIsHost] = useState(false);  // Gestisci lo stato per determinare se l'utente è un host

  // Funzione per gestire il login e aggiornare lo stato
  const handleLogin = (username, password) => {
    if (username === 'admin' && password === 'password') {
      setIsHost(true);  // Se l'host è loggato, aggiorniamo lo stato
    }
  };

  return (
    <Router>
      <Navbar isHost={isHost} />  {/* Passa isHost alla Navbar */}
      <main>
        <Routes>
          <Route path="/" element={<><Hero /><AboutUs /><Testimonials /></>} />
          <Route path="/corsi" element={<Corsi />} />
          <Route path="/servizi" element={<Servizi />} />
          <Route path="/contatti" element={<Contatti />} />
          <Route path="/recensioni" element={<Recensioni />} />
          <Route path="/GestioneRecensioni" element={<GestioneRecensioni />} />
          <Route path="/host-login" element={<HostLogin onLogin={handleLogin} />} />
        </Routes>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
