import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Recensioni.css'; // Importiamo il CSS per il miglioramento dello stile

const Recensioni = () => {
  const [recensioni, setRecensioni] = useState([]);
  const [nomeCliente, setNomeCliente] = useState('');
  const [recensione, setRecensione] = useState('');
  const [valutazione, setValutazione] = useState(5);

  // Carica le recensioni dal backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni')
      .then(response => {
        setRecensioni(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni:', error);
      });
  }, []);

  // Funzione per inviare una nuova recensione
  const inviaRecensione = (e) => {
    e.preventDefault();

    const nuovaRecensione = {
      nome_cliente: nomeCliente,
      recensione,
      valutazione,
    };

    // Invia la recensione al backend
    axios.post('http://localhost:5000/api/recensioni', nuovaRecensione)
      .then(response => {
        alert('Recensione inviata con successo!');
        setRecensioni([...recensioni, response.data]); // Aggiungi la nuova recensione alla lista
        setNomeCliente('');
        setRecensione('');
        setValutazione(5);
      })
      .catch(error => {
        console.error('Errore nell\'invio della recensione:', error);
        alert('Si è verificato un errore. Riprova più tardi.');
      });
  };

  return (
    <div className="recensioni-container">
      <h2>Recensioni</h2>

      {/* Elenco delle recensioni */}
      <div className="recensioni-lista">
        {recensioni.map((recensione) => (
          <div className="recensione" key={recensione.id}>
            <h3>{recensione.nome_cliente}</h3>
            <p>{recensione.recensione}</p>
            <p><strong>Valutazione: </strong>{recensione.valutazione}★</p>
          </div>
        ))}
      </div>

      {/* Form per aggiungere una nuova recensione */}
      <div className="form-recensione">
        <h3>Aggiungi una Recensione</h3>
        <form onSubmit={inviaRecensione}>
          <div className="form-field">
            <label htmlFor="nomeCliente">Nome Cliente</label>
            <input 
              type="text" 
              id="nomeCliente" 
              value={nomeCliente} 
              onChange={(e) => setNomeCliente(e.target.value)} 
              required 
            />
          </div>
          <div className="form-field">
            <label htmlFor="recensione">Recensione</label>
            <textarea 
              id="recensione" 
              value={recensione} 
              onChange={(e) => setRecensione(e.target.value)} 
              required 
            />
          </div>
          <div className="form-field">
            <label htmlFor="valutazione">Valutazione (1-5)</label>
            <input 
              type="number" 
              id="valutazione" 
              min="1" 
              max="5" 
              value={valutazione} 
              onChange={(e) => setValutazione(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" className="btn-submit">Invia Recensione</button>
        </form>
      </div>
    </div>
  );
};

export default Recensioni;
