import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GestioneRecensioni() {
    const [recensioni, setRecensioni] = useState([]);

    useEffect(() => {
        // Recupera le recensioni in attesa di approvazione
        axios.get('http://localhost:5000/api/recensioni-in-attesa')
            .then(response => {
                setRecensioni(response.data);
            })
            .catch(error => {
                console.error('Errore nel recupero delle recensioni', error);
            });
    }, []);

    // Funzione per approvare la recensione
    const approvaRecensione = (id) => {
        axios.put('http://localhost:5000/api/approva-recensione', { id })
            .then(response => {
                alert(response.data);
                setRecensioni(prevRecensioni => prevRecensioni.filter(rec => rec.id !== id));
            })
            .catch(error => {
                console.error('Errore nell\'approvazione della recensione', error);
            });
    };

    // Funzione per rifiutare la recensione
    const rifiutaRecensione = (id) => {
        axios.delete('http://localhost:5000/api/rifiuta-recensione', { data: { id } })
            .then(response => {
                alert(response.data);
                setRecensioni(prevRecensioni => prevRecensioni.filter(rec => rec.id !== id));
            })
            .catch(error => {
                console.error('Errore nell\'eliminazione della recensione', error);
            });
    };

    return (
        <div>
            <h2>Gestione delle Recensioni</h2>
            <ul>
                {recensioni.map(recensione => (
                    <li key={recensione.id}>
                        <p><strong>Utente:</strong> {recensione.user_id}</p>
                        <p><strong>Recensione:</strong> {recensione.recensione}</p>
                        <button onClick={() => approvaRecensione(recensione.id)}>Accetta</button>
                        <button onClick={() => rifiutaRecensione(recensione.id)}>Rifiuta</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default GestioneRecensioni;
