import React from 'react';
import './Contatti.css';

const Contatti = () => {
  // WhatsApp number (change this to your WhatsApp number)
  const whatsappNumber = '+393663691564';
  const message = 'Ciao, ho bisogno di maggiori informazioni!'; // Default message

  const whatsappLink = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`;

  return (
    <div className="contatti-page">
      <h1>Contattaci</h1>

      {/* WhatsApp Card */}
      <div className="whatsapp-card" onClick={() => window.open(whatsappLink, "_blank")}>
        <i className="fab fa-whatsapp"></i>
        <h3>Hai domande?</h3>
        <p>Contattaci subito tramite WhatsApp!</p>
        <button className="whatsapp-button">Invia Messaggio</button>
      </div>

      {/* Contact Information Section */}
      <div className="contact-info">
        <h2>Informazioni di Contatto</h2>
        <p><i className="fas fa-map-marker-alt"></i> <strong>Indirizzo:</strong> Via Antonio Gramsci n. 36, Melito di Porto Salvo</p>
        <p><i className="fas fa-envelope"></i> <strong>Email:</strong> beautyarmonyacademy@gmail.com</p>
        <p><i className="fas fa-phone"></i> <strong>Telefono:</strong> +39 366 369 1564</p>
        <p><i className="fas fa-clock"></i> <strong>Orari:</strong> Lunedì - Venerdì: 09:00 - 18:00</p>
        <p><i className="fas fa-globe"></i> <strong>Sito web:</strong> <a href="https://www.beautyarmonyacademy.com" target="_blank" rel="noopener noreferrer">www.beautyarmonyacademy.com</a></p>
      </div>
    </div>
  );
};

export default Contatti;
