import React, { useState } from 'react';
import './Contatti.css';

const Contatti = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    messaggio: '',
  });

  const [successo, setSuccesso] = useState('');
  const [errore, setErrore] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.nome || !formData.email || !formData.messaggio) {
      setErrore('Tutti i campi sono obbligatori!');
      return;
    }

    fetch('http://localhost:5000/api/invia-messaggio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'Messaggio inviato con successo!') {
          setSuccesso('Messaggio inviato con successo!');
          setErrore('');
          setFormData({ nome: '', email: '', messaggio: '' });
        } else {
          throw new Error('Errore nell\'invio del messaggio');
        }
      })
      .catch((err) => {
        setErrore(`Si è verificato un errore: ${err.message}`);
        setSuccesso('');
        console.log(err);
      });
  };

  return (
    <div className="contatti-page">
      <section className="contatti-section">
        <h1>Contattaci</h1>
        <p>Hai domande o richieste? Siamo qui per aiutarti!</p>

        <div className="contatti-form">
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <label htmlFor="nome">Nome</label>
              <input
                type="text"
                id="nome"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="messaggio">Messaggio</label>
              <textarea
                id="messaggio"
                name="messaggio"
                value={formData.messaggio}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="btn invia">
              Invia Messaggio
            </button>
          </form>

          {errore && <div className="errore">{errore}</div>}
          {successo && <div className="successo">{successo}</div>}
        </div>

        <div className="contatti-info">
          <h2>Informazioni di contatto</h2>
          <p><strong>Indirizzo:</strong> Via Esempio 123, Melito di Porto Salvo</p>
          <p><strong>Email:</strong> info@beautyarmony.com</p>
          <p><strong>Telefono:</strong> +39 123 456 789</p>
        </div>
      </section>
    </div>
  );
};

export default Contatti;
