import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ isHost, onLogout }) => {
  const [isMobile, setIsMobile] = useState(false);

  const toggleMenu = () => {
    setIsMobile(!isMobile);
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>MyWebsite</div>

      {/* Pulsante mobile */}
      <button onClick={toggleMenu} style={styles.mobileMenuButton}>☰</button>

      {/* Menu */}
      <ul style={{ ...styles.navList, display: isMobile ? 'flex' : 'none' }}>
        <li style={styles.navItem}>
          <Link to="/" style={styles.navLink}>Home</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/servizi" style={styles.navLink}>Servizi</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/corsi" style={styles.navLink}>Corsi</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/recensioni" style={styles.navLink}>Recensioni</Link>
        </li>

        {/* Aggiungi il pulsante solo se l'utente è un host */}
        {isHost && (
          <li style={styles.navItem}>
            <Link to="/GestioneRecensioni" style={styles.navLink}>Gestione Recensioni</Link>
          </li>
        )}

        {/* Aggiungi il pulsante di logout solo se l'utente è loggato */}
        {isHost && (
          <li style={styles.navItem}>
            <button onClick={onLogout} style={styles.logoutButton}>Logout</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1.5rem 3rem',
    background: 'linear-gradient(135deg, #f8b400, #f05d23)',
    color: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderBottom: '3px solid #fff',
  },
  logo: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    letterSpacing: '1px',
    color: '#fff',
  },
  navList: {
    listStyle: 'none',
    display: 'flex',
    gap: '2rem',
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: '0 10px',
  },
  navLink: {
    textDecoration: 'none',
    color: '#fff',
    fontWeight: 'bold',
    fontSize: '1.2rem',
    transition: 'color 0.3s, transform 0.2s',
  },
  logoutButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#e74c3c',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  mobileMenuButton: {
    display: 'none',
    fontSize: '2rem',
    color: '#fff',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
  },
  '@media (max-width: 768px)': {
    mobileMenuButton: {
      display: 'block', // Mostra il pulsante solo su dispositivi mobili
    },
  },
};

export default Navbar;
