import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './GestioneRecensioni.css';

function GestioneRecensioni() {
  const [recensioniInAttesa, setRecensioniInAttesa] = useState([]); // Stato corretto per le recensioni in attesa
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/recensioni-pending') // Modifica se l'endpoint è diverso
      .then(response => {
        setRecensioniInAttesa(response.data);
      })
      .catch(error => {
        console.error('Errore nel recupero delle recensioni in attesa:', error);
        setError('Errore nel recupero delle recensioni in attesa');
      });
  }, []);
  

  const gestisciRecensione = (id, stato) => {
    // Gestisci la recensione (approvazione o rifiuto)
    axios.post('http://localhost:5000/api/gestisci-recensione', { id, stato })
      .then(() => {
        // Aggiorna lo stato locale eliminando la recensione gestita
        setRecensioniInAttesa(recensioniInAttesa.filter(recensione => recensione.id !== id));
      })
      .catch(() => setError('Errore nella gestione della recensione'));
  };

  return (
    <div className="gestione-recensioni">
      <h2>Gestione Recensioni</h2>
      {error && <div className="error">{error}</div>}

      <ul>
        <h3>Recensioni in Attesa:</h3>
        {recensioniInAttesa.length === 0 ? (
          <p>Nessuna recensione in attesa</p>
        ) : (
          recensioniInAttesa.map((recensione) => (
            <li key={recensione.id}>
              <p><strong>Nome Utente:</strong> {recensione.nome_cliente}</p>
              <p><strong>Recensione:</strong> {recensione.recensione}</p>
              <p><strong>Rating:</strong> {recensione.valutazione}</p>
              <button onClick={() => gestisciRecensione(recensione.id, 'approva')}>Approva</button>
              <button onClick={() => gestisciRecensione(recensione.id, 'rifiuta')}>Rifiuta</button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default GestioneRecensioni;
