import React from 'react';
import logo from './logo.png'; // Importa il logo se è nella cartella assets

const Hero = () => {
  return (
    <div style={styles.hero}>
      <div style={styles.overlay}>
        <div style={styles.textContainer}>
          <img src={logo} alt="Beauty Armony Academy Logo" style={styles.logo} />
          <h1 style={styles.heading}>Benvenuti alla Beauty Armony Academy</h1>
          <p style={styles.subheading}>Scopri il nostro programma di corsi professionali per l'estetica e i trattamenti di bellezza.</p>
          <button style={styles.button}>Scopri i Corsi</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  hero: {
    background: 'url("https://yourimageurl.com") no-repeat center center',
    backgroundSize: 'cover',
    height: '100vh',
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff',
    textAlign: 'center',
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'rgba(0, 0, 0, 0.5)', // Semi-transparent overlay for better readability
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  textContainer: {
    maxWidth: '700px',
    padding: '3rem',
    backgroundColor: 'rgba(0, 0, 0, 0.6)', // Slightly darker background for text contrast
    borderRadius: '10px',
  },
  logo: {
    width: '150px',
    marginBottom: '20px',
  },
  heading: {
    fontSize: '3rem',
    fontWeight: 'bold',
    margin: '0',
  },
  subheading: {
    fontSize: '1.2rem',
    marginTop: '1rem',
    marginBottom: '2rem',
  },
  button: {
    padding: '1rem 2rem',
    backgroundColor: '#f8b400',
    color: '#fff',
    border: 'none',
    fontSize: '1.1rem',
    cursor: 'pointer',
    borderRadius: '5px',
    transition: 'background-color 0.3s, transform 0.3s',
  },
  buttonHover: {
    backgroundColor: '#f05d23',
    transform: 'scale(1.05)',
  },
};

export default Hero;
