import React from 'react';
import { Link } from 'react-router-dom';
import logo from './assets/logo.png'; // Assicurati che il percorso sia corretto

const Navbar = () => {
  return (
    <nav style={styles.nav}>
      <div style={styles.logo}>
        <img src={logo} alt="Beauty Armony Academy" style={styles.logoImage} />
      </div>
      <ul style={styles.menu}>
        <li><Link to="/" style={styles.link}>Home</Link></li>
        <li><Link to="/corsi" style={styles.link}>Corsi</Link></li>
        <li><Link to="/servizi" style={styles.link}>Servizi</Link></li>
        <li><Link to="/contatti" style={styles.link}>Contatti</Link></li>
        <li><Link to="/recensioni" style={styles.link}>Recensioni</Link></li>
        <li><Link to="/GestioneRecensioni" style={styles.link}>Gestione Recensioni</Link></li>
      </ul>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    background: 'linear-gradient(135deg, #f8b400, #f05d23)', // Gradiente dal giallo all'arancione
    color: '#fff',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    borderBottom: '2px solid #fff',
  },
  logo: {
    flex: 1,
    textAlign: 'center', // Centra il logo
  },
  logoImage: {
    height: '40px', // Imposta l'altezza del logo
    objectFit: 'contain', // Mantiene le proporzioni dell'immagine
  },
  menu: {
    listStyle: 'none',
    display: 'flex',
    gap: '1.5rem',
    margin: 0,
    padding: 0,
    flex: 2,
    justifyContent: 'flex-end', // Allinea i menu a destra
  },
  link: {
    textDecoration: 'none',
    color: '#fff',
    fontWeight: 'bold',
    fontSize: '1rem',
    transition: 'color 0.3s, transform 0.2s',
  },
  linkHover: {
    color: '#f05d23',
    transform: 'scale(1.1)',
  },
};

export default Navbar;
