// src/pages/Contatti.js
import React, { useState } from 'react';
import './Contatti.css';

const Contatti = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    messaggio: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Qui dovresti implementare la logica per inviare il modulo (es. API o email)
    alert('Messaggio inviato!');
  };

  return (
    <div className="contatti-page">
      <section className="contatti-section">
        <h1>Contattaci</h1>
        <p>Hai domande o richieste? Siamo qui per aiutarti!</p>

        <div className="contatti-form">
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <label htmlFor="nome">Nome</label>
              <input
                type="text"
                id="nome"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label htmlFor="messaggio">Messaggio</label>
              <textarea
                id="messaggio"
                name="messaggio"
                value={formData.messaggio}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="btn invia">Invia Messaggio</button>
          </form>
        </div>

        <div className="contatti-info">
          <h2>Informazioni di contatto</h2>
          <p><strong>Indirizzo:</strong> Via Esempio 123, Melito di Porto Salvo</p>
          <p><strong>Email:</strong> info@beautyarmony.com</p>
          <p><strong>Telefono:</strong> +39 123 456 789</p>
        </div>
      </section>
    </div>
  );
};

export default Contatti;
