import React from 'react';
import logo from './logo.png'; // Importa il logo se è nella cartella assets

const Hero = () => {
  return (
    <div style={styles.hero}>
      <div style={styles.textContainer}>
        <img src={logo} alt="Beauty Armony Academy Logo" style={styles.logo} />
        <h1 style={styles.heading}>Benvenuti alla Beauty Armony Academy</h1>
        <p style={styles.subheading}>Scopri il nostro programma di corsi professionali per l'estetica e i trattamenti di bellezza.</p>
        <button style={styles.button}>Scopri i Corsi</button>
      </div>
    </div>
  );
};

const styles = {
  hero: {
    background: 'url("https://yourimageurl.com") no-repeat center center',
    backgroundSize: 'cover',
    height: '100vh',
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff',
    textAlign: 'center',
    padding: '0 20px', // Aggiunta spaziatura per evitare che il contenuto tocchi i bordi
  },
  textContainer: {
    maxWidth: '700px',
    padding: '3rem',
    backgroundColor: 'rgba(0, 0, 0, 0.6)', // Background scuro per migliorare il contrasto
    borderRadius: '10px',
    textAlign: 'center',
  },
  logo: {
    width: '180px', // Logo di dimensioni più grandi
    marginBottom: '20px',
  },
  heading: {
    fontSize: '3.5rem',
    fontWeight: 'bold',
    marginBottom: '1rem', // Più spazio tra titolo e testo
  },
  subheading: {
    fontSize: '1.2rem',
    marginBottom: '2rem',
    fontWeight: '300',
  },
  button: {
    padding: '1rem 2rem',
    backgroundColor: '#f8b400',
    color: '#fff',
    border: 'none',
    fontSize: '1.1rem',
    cursor: 'pointer',
    borderRadius: '5px',
    transition: 'background-color 0.3s, transform 0.3s',
  },
  buttonHover: {
    backgroundColor: '#f05d23',
    transform: 'scale(1.05)',
  },
};

export default Hero;
