import React from 'react';
import Slider from 'react-slick';

const AboutUs = () => {
  const settings = {
    dots: true, 
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Attiva l'autoplay
    autoplaySpeed: 3000, // Imposta il tempo tra ogni slide
    fade: true, // Transizione di dissolvenza tra le slide
    arrows: false, // Disabilita le frecce
  };

  return (
    <section style={styles.section}>
      <h2 style={styles.heading}>Chi Siamo</h2>
      <p style={styles.text}>
        La Beauty Armony Academy, fondata da Roberta Caruso a Melito di Porto Salvo, Ã¨ un centro di formazione all'avanguardia nel campo dell'estetica e dei trattamenti di bellezza. Offriamo corsi pratici e teorici, pensati per chi desidera intraprendere una carriera professionale nell'estetica.
      </p>
      <div style={styles.imgContainer}>
        <Slider {...settings}>
          <div style={styles.slide}>
            <img src="https://yourimageurl.com/academy-image1.jpg" alt="Beauty Armony Academy 1" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://yourimageurl.com/academy-image2.jpg" alt="Beauty Armony Academy 2" style={styles.img} />
          </div>
          <div style={styles.slide}>
            <img src="https://yourimageurl.com/academy-image3.jpg" alt="Beauty Armony Academy 3" style={styles.img} />
          </div>
        </Slider>
      </div>
      <div style={styles.buttonContainer}>
        <button style={styles.button}>Scopri i Corsi</button>
      </div>
    </section>
  );
};

const styles = {
  section: {
    padding: '4rem 2rem',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #f8b400, #ff5722)',
    color: '#fff',
    borderRadius: '10px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: '1.5rem',
    letterSpacing: '1px',
  },
  text: {
    fontSize: '1.2rem',
    maxWidth: '800px',
    margin: '0 auto',
    marginBottom: '2rem',
    lineHeight: '1.6',
    color: '#fff',
  },
  imgContainer: {
    maxWidth: '800px',
    margin: '0 auto',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.2)',
  },
  slide: {
    position: 'relative',
  },
  img: {
    width: '100%',
    height: 'auto',
    borderRadius: '10px',
    objectFit: 'cover',
    transition: 'transform 0.5s ease-in-out',
  },
  buttonContainer: {
    marginTop: '2rem',
  },
  button: {
    padding: '0.8rem 2rem',
    background: 'linear-gradient(135deg, #f8b400, #ff5722)',
    color: '#fff',
    border: 'none',
    borderRadius: '50px',
    fontSize: '1.1rem',
    cursor: 'pointer',
    transition: 'transform 0.3s, box-shadow 0.3s',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  },
  buttonHover: {
    transform: 'scale(1.1)',
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.3)',
  },
};

export default AboutUs;
