// src/pages/Recensioni.js
import React, { useState } from 'react';
import './Recensioni.css';

const Recensioni = () => {
  const [formData, setFormData] = useState({
    nome: '',
    recensione: '',
  });

  const [recensioni, setRecensioni] = useState([
    {
      nome: 'Giulia R.',
      recensione: 'Un’esperienza fantastica! Ho seguito il corso di estetica e ho imparato tantissimo. Consigliatissimo!',
    },
    {
      nome: 'Marco T.',
      recensione: 'L’ambiente è accogliente e i docenti sono molto preparati. Sono uscito con nuove competenze e molta fiducia.',
    },
    {
      nome: 'Sara L.',
      recensione: 'Un luogo perfetto per imparare e crescere. I corsi sono ben strutturati e i servizi eccellenti.',
    },
  ]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setRecensioni([
      ...recensioni,
      { nome: formData.nome, recensione: formData.recensione },
    ]);
    setFormData({ nome: '', recensione: '' });
  };

  return (
    <div className="recensioni-page">
      <section className="recensioni-section">
        <h1>Le Nostre Recensioni</h1>
        <p>Leggi le esperienze dei nostri clienti soddisfatti!</p>

        <div className="recensioni-list">
          {recensioni.map((recensione, index) => (
            <div key={index} className="recensione">
              <p><strong>{recensione.nome}</strong></p>
              <p>{recensione.recensione}</p>
            </div>
          ))}
        </div>

        <h2>Lascia la tua recensione</h2>
        <form onSubmit={handleSubmit} className="recensione-form">
          <div className="input-group">
            <label htmlFor="nome">Nome</label>
            <input
              type="text"
              id="nome"
              name="nome"
              value={formData.nome}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="recensione">Recensione</label>
            <textarea
              id="recensione"
              name="recensione"
              value={formData.recensione}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn invia">Invia Recensione</button>
        </form>
      </section>
    </div>
  );
};

export default Recensioni;
